INSERT INTO `manual` VALUES ('[{index:0,content:\'首次加载或者点击下一步会自动在输入框加载提示，可选择直接发送，或者[微调]提示文本内容，直到得到理想的结果\'},{index:1,content:\'某些提示需要用户输入语料，请注意检查下方提示，确认所有语料均完成。语料的输入方式为：将输入框中${{x}}$内部的x替换为你需要输入的预料文本\'},{index:2,content:\'需要的语料没有完全输入的情况下无法进入下一步\'},{index:3,content:\'当用户在一个步骤中进行了多轮对话的时候，需要手动将其中一项返回的文本标记为理想文本，便于后续步骤自动作为语料输入\'},{index:4,content:\'尽管提供了可调节temperature和n两个参数的功能，但是建议保持默认值\'},{index:5,content:\'不要删除${{}}$符号，请直接将内容填入其中即可。需要用户输入预料的位置用红色加粗的${{}}$符号标记出\'}]');
