INSERT INTO `pattern_config` VALUES ('026a4797-3f14-4fdc-ada2-10383e599893','{\"steps\": [{\"id\": \"f9beaf35-91a3-474c-a40c-f44279c729e2\", \"desc\": \"分析优势与不足之处，然后根据不足的方面生成改进建议的文本内容，并对文本内容的字数提出了限制\", \"name\": \"获得改进建议文本内容\", \"needed\": 1, \"prompts\": [{\"id\": \"8859150e-eee3-45d5-bffe-7a503498835b\", \"desc\": \"学校在某个指标中的表现，如果下面有许多二级指标，需要把二级指标的表现文本都带上。\", \"name\": \"指定角色，分配任务\", \"relyid\": \"\", \"content\": \"现在你是一位教育专家，你要根据下面给出的有关某所中学的办学质量评价的文本内容，分析这所中学在那些方面表现的好，在哪些方面表现的差。然后综合分析这所中学表现差的方面，为这所中学写一段改进建议，改进建议的字数不要低于600字。\", \"category\": 0}, {\"id\": \"50243388-19db-4542-a7ff-668a1451003b\", \"name\": \"某中学在某一级指标上的排名等描述性文本内容\", \"des c\": \"学校在某个指标中的表现，如果下面有许多二级指标，需要把二级指标的表现文本都带上。\", \"relyid\": \"\", \"content\": \"\", \"category\": 1}]}, {\"id\": \"7d55f521-fd8c-42a0-a3b1-5c90645f382e\", \"desc\": \"得到一条改进建议的标题\", \"name\": \"生成改进建议标题\", \"needed\": 0, \"prompts\": [{\"id\": \"9a944e49-f0ca-4bae-a2da-94d5c8c5a608\", \"desc\": \"说明任务\", \"name\": \"说明任务，即对文本进行摘要，进而得到标题\", \"relyid\": \"f9beaf35-91a3-474c-a40c-f44279c729e2\", \"content\": \"下面是专家为某所中学提出的改进建议文本内容，请将这些内容概括为若干个短句，例如深入推进国家课程校本化实施，提升领导团队课程领导力，促进学校内涵发展、提升领导团队的课程教学领导力，促进学校内涵发展等。\", \"category\": 0}, {\"id\": \"c3b7c40e-4e45-4c8a-b736-3db2d67b8398\", \"desc\": \"由上一步生成的改进建议文本内容\", \"name\": \"改进建议文本\", \"relyid\": \"f9beaf35-91a3-474c-a40c-f44279c729e2\", \"content\": \"\", \"category\": 2}]}, {\"id\": \"24361ecd-cb5b-43af-874e-4bc85a99eb50\", \"desc\": \"将该指标内涵的文本部分融入到改进建议文本当中，使得改进建议当中包含部分阐述改进重要性的文本内容。\", \"name\": \"将上位概念融入改进建议文本内容中\", \"needed\": 1, \"prompts\": [{\"id\": \"3739de33-8ab7-4299-8f68-7135455e5165\", \"desc\": \"某个一级指标以及下面的二级指标的内涵、意义描述文本。可以从政策文本中引用。\", \"name\": \"派送任务\", \"relyid\": \"\", \"content\": \"下面的文本内容一是对一些指标的重要性的阐释，文本内容二是某位专家为某所中学提出的办学改进建议。现在你要以文本内容二为主，从文本内容一中选择一些内容并融入到文本内容二中，形成一段不少于600字的文本，注意不要改变文本内容二的意思。文本内容一如下：\", \"category\": 0}, {\"id\": \"d4e882f1-9357-44d7-979c-4aa7b0ee7dc5\", \"desc\": \"某个一级指标以及下面的二级指标的内涵、意义描述文本。可以从政策文本中引用。\", \"name\": \"阐释某项评价指标的内涵、意义的文本\", \"relyid\": \"\", \"content\": \"\", \"category\": 1}, {\"id\": \"64723a30-a0de-4276-8648-fd99101c9a7f\", \"desc\": \"某个一级指标以及下面的二级指标的内涵、意义描述文本。可以从政策文本中引用。\", \"name\": \"派送任务\", \"relyid\": \"\", \"content\": \"文本内容二如下：\", \"category\": 0}, {\"id\": \"d649f867-e6a1-4c10-8e65-ba4471fb181e\", \"desc\": \"由第一步生成的改进建议的文本内容\", \"name\": \"已经生成的改进建议文本\", \"relyid\": \"f9beaf35-91a3-474c-a40c-f44279c729e2\", \"content\": \"\", \"category\": 2}]}, {\"id\": \"690fcdf3-3ba1-45c1-8857-8c5464ff5c30\", \"desc\": \"将学校目前的真实表现情况文本部分融入到改进建议文本当中，使得改进建议文本当中包含对学校具体情况的分析语句。\", \"name\": \"结合学校实际修改学校改进建议的文本内容\", \"needed\": 1, \"prompts\": [{\"id\": \"fc819f30-096d-46c8-923a-c083daafbace\", \"desc\": \"告知模型应该完成的内容\", \"name\": \"派送任务\", \"relyid\": \"\", \"content\": \"下面的文本内容一是某所中学在实际的教学工作中的自我评价，文本内容二是一段为该中学在办学方面需要改进的内容提出的改进建议。现在你要根据文本内容一分析这所中学有哪些优势，然后将这些优势适当的加入到文本内容二中，使得文本内容二不仅仅包含了对这所中学表现的不好的方面的改进建议，还有对这所学校表现的好的方面的肯定。注意整合后的文本不要偏离文本内容二的意思，整合后的文本的字数不少于600字。文本内容一如下：\", \"category\": 0}, {\"id\": \"92ee9198-7e11-4935-a768-12312341657d\", \"desc\": \"学校在各个方面的表现的文本描述\", \"name\": \"学校实际办学表现的文本内容\", \"relyid\": \"\", \"content\": \"\", \"category\": 1}, {\"id\": \"57eb044f-b1b2-441b-8370-cf2d8fc795cd\", \"desc\": \"\", \"name\": \"文本内容二提示文本\", \"relyid\": \"\", \"content\": \"文本内容二如下：\", \"category\": 0}, {\"id\": \"3e1d9150-326b-4fc2-af9b-1cd6a06fde88\", \"desc\": \"由上一步得到的改进建议文本\", \"name\": \"融合了概念文本的改进建议内容\", \"relyid\": \"24361ecd-cb5b-43af-874e-4bc85a99eb50\", \"content\": \"\", \"category\": 2}]}, {\"id\": \"b9fe5962-2fa6-4e9e-9114-ee25559745d9\", \"desc\": \"调整改进建议的用词，以及改进建议的语言风格等。\", \"name\": \"调整改进建议文本的用词、语言风格、通顺程度等\", \"needed\": 0, \"prompts\": [{\"id\": \"2eca438c-52f8-460e-9ab2-1af05c6b9407\", \"desc\": \"告知模型要调整文本内容\", \"name\": \"派送任务\", \"relyid\": \"\", \"content\": \"以下内容是某位专家对某个中学的办学提出的改进建议文本。你要对文本内容进行修改，在不改变文本原有的意思的前提下，确保修改后的文本内容中没有人称代词，保证文本的语气要温和、要体现[建议]而不是[批评]。\", \"category\": 0}, {\"id\": \"369fb1e0-025a-4932-a694-d80e2d9fa059\", \"desc\": \"融合了指标含义、概念，以及包含了学校实际办学情况的改进建议文本内容，由上一步得到\", \"name\": \"修改了两次之后的改进建议文本内容\", \"relyid\": \"690fcdf3-3ba1-45c1-8857-8c5464ff5c30\", \"content\": \"\", \"category\": 2}]}]}','2023-04-15 12:51:18'),('07c4eaf9-68e2-472c-bc26-23a314e95628','{\"steps\": [{\"id\": \"6c3c5a8c-6501-4f4b-bb23-75b85c3b2f55\", \"desc\": \"办学方面简易\", \"name\": \"办学方向方面\", \"needed\": 0, \"prompts\": [{\"id\": \"cb1d3864-6fb1-4747-a434-1cd7f66d323a\", \"name\": \"定义角色\", \"content\": \"作为教育评价专家，请根据以下学校的情况提出改进建议。这所学校的不同方面的表现排名数据如下：\", \"category\": 0}, {\"id\": \"a16ca8a2-b922-4c6c-beef-6855f9be52e2\", \"name\": \"排名数据\", \"category\": 1}, {\"id\": \"7eaaa8c3-a7c5-4aaf-a078-71502ad68391\", \"name\": \"学校总数\", \"content\": \"共有42所学校参与排名。\", \"category\": 0}, {\"id\": \"f1c41fc3-1efb-45cb-abc6-4953d320d0a7\", \"name\": \"非常薄弱的方面\", \"content\": \"该校在以下方面表现较差：。需要特别关注这些表现较差的方面，并在建议中特别说明针对这些表现较差的方面的解决方案。\", \"category\": 0}, {\"id\": \"81280e8a-f19f-4106-a55e-632b32d6e8d0\", \"name\": \"参考文本\", \"content\": \"参考以下3个评价方案的内容与结构，根据学校实际情况，提供针对性建议，字数500字。确保建议与学校实际情况相符，勿提供虚假信息。 评价方案1是针对各方面表现优秀的学校（各方面排名均位于前三分之一）。建议：凝练学校文化，确立学校发展的卓越精神。学校改进应以凝练学校文化，确立学校发展的卓越精神为主，可从以下几方面入手：（1）在加强党建工作方面，需要学校领导带领全体教职员工梳理学校发展的高标意识和价值追求，共同总结梳理学校辉煌办学的经验、学校进一步发展面临的优势和不足、机遇与挑战，探索学校高品质发展的路径和策略。在此过程中，激发教师团队的奋斗精神、使命意识、危机意识以及校荣我荣的主人翁精神，打造积极进取、追求卓越的学校精神，鼓励教职员工的创新意识和能力。（2）在坚持立德树人方面，充分阐发优秀传统文化在现代教育语境中的意涵，并使之深入关联、贯穿于学校的管理思路、德育工作、课程教学、评价体系及家校协作中，将条块工作落实到具体职能部门，打通办学思路和组织架构之间的常见隔阂。如此学校全局工作才有可能纲举目张，事半功倍。 评价方案2是针对表现一般的学校（各方面排名均位于中间三分之一）。建议：完善党建责任机制，实现学校内涵式发展。学校改进应以实现学校内涵式发展为主，可从以下几方面入手：（1）在加强党建工作方面，应当注意融冶中华传统文化、地方文化（包括民族文化）、学校文化中的优秀元素，并在建设方案中将呈现在校园环境中的物质层面文化、凝聚在学校管理中的制度层面文化，以及内在于师生精神气质的思想层面文化，进行整体规划推进，将校园文化建设全面融贯于学校工作。（2）在坚持立德树人方面，进一步加强学校特色建设，组织一些具有学校特色的活动，如文艺比赛、科技展览、社会实践等，增强学生对学校文化的认同感。开展丰富多彩的德育活动，注重学生的综合素质培养，培养学生正确的人生观和价值观。 注重学生的全面发展，提供多元化的教育资源和个性化的教育服务，满足不同学生的学习需求，促进学生的全面成长。 评价方案3是针对表现较差的学校（各方面排名均位于后三分之一）。建议：加强党的全面领导，优化学校顶层设计。学校改进应以进一步加强党的全面领导、优化学校顶层设计为主，可从以下几方面入手：（1）在加强党建工作方面，进一步明确学校价值观，通过凝练学校的价值观，明确学校的核心价值和文化，使教师、学生、家长等各方面对学校文化的认同感更强。建议学校领导团队必要时加以梳理，从核心立校精神出发，推演阐发学校办学理念以及学校文化，而后以此种精神及理念，一以贯之地呈现在学校管理、德育、教学等核心工作之中，避免规划方案与实际执行之间可能的脱节与疏离。（2）在坚持立德树人方面，增加学校特色课程，通过开设一些特色课程，如艺术课、体育课、科技课等，让学生在学习的过程中体验学校的文化和精神。建立积极向上的氛围，鼓励学生以积极向上的态度面对学习和生活，创造良好的学校氛围，让学校文化更具凝聚力。注重思想道德教育的重要性，加强学生的思想道德素质，引导学生树立正确的人生观、价值观和道德观。参考上述3个评价方案时，注意根据这所学校每个方面的实际排名情况，从不同的评价方案中选择与之相对应的建议。不要简单的重复评价方案的句子。\", \"category\": 0}]}, {\"id\": \"764df2ff-c6c1-4285-8974-6c0a3f581e47\", \"name\": \"课程教学方面\", \"needed\": 1, \"prompts\": [{\"id\": \"2254837d-cd57-48d6-8c94-d334fd6dffd4\", \"name\": \"定义角色\", \"content\": \"作为教育评价专家，请根据以下学校的情况提出改进建议。这所学校的不同方面的表现排名数据如下：\", \"category\": 0}, {\"id\": \"901fdf32-4f8e-494d-ad72-d964829c2f19\", \"name\": \"输入排名数据\", \"category\": 1}, {\"id\": \"03042526-c9f5-47d4-b20a-3072d3bd5011\", \"name\": \"学校总数\", \"content\": \"一共有36所学校参与排名。\", \"category\": 0}, {\"id\": \"4fca3024-e349-4624-b747-febee2b05988\", \"name\": \"非常薄弱的方面\", \"content\": \"该校在以下方面表现较差：。需要特别关注这些表现较差的方面，并在建议中特别说明针对这些表现较差的方面的解决方案。\", \"category\": 0}, {\"id\": \"c498efc5-1b20-4cf3-b3a9-9a04cbfe6448\", \"name\": \"薄弱方面后缀\", \"content\": \"参考以下3个评价方案的内容与结构，根据学校实际情况，提供针对性建议，字数500字。确保建议与学校实际情况相符，勿提供虚假信息。 评价方案1是针对各方面表现优秀的学校（各方面排名均位于前三分之一）。建议：丰富和完善校本课程，进一步突显办学特色。学校改进应以进一步突显特色为主，可从以下几方面入手：（1）在落实课程方案方面，完善不同类型课程之间的内在逻辑结构，优化各类课程互相之间的关联与支撑，在课程教学中落实五育并举的要求，促进学生全面均衡发展。（2）在规范教学实施方面，完善各类课程的综合评价改革方案，探索面向全体老师、学生，并注重分层次、类型的评价标准。通过评价方案改革，充分调动师生的积极性，激活发展潜力。（3）在优化教学方式方面，可进一步注重启发式、互动式、探究式教学，推进信息技术与教育教学深度融合。鼓励师生充分利用网上资源，由文本信息延伸、扩展到科技发展前沿的动态和当今世界优秀的文化成果。 评价方案2是针对表现一般的学校（各方面排名均位于中间三分之一）。建议：深化课堂教学改革，提升课堂教学效益。学校改进应以提升效益为主，可从以下几方面入手：（1）在落实课程方案方面，继续深入推进国家课程的校本化实施，具体路径为：基于国家课程教学的要求和学校学生的基本情况，借助市教研员的专业指导，编制各学科教学指南，形成各学科校本的教学资料库、学习资料库、学生习题库，将国家课程要求与本校实际紧密结合。（2）在规范教学实施方面，进一步加强教学研究，借助市区学科教学专家的力量，结合各学科的教学特点，梳理总结出各学科、各课型自主高效的课堂的流程与结构。（3）在优化教学方式方面，充分利用信息技术，提升课堂教学体验和效益。利用学校已有的信息技术设备和条件，进一步建设和完善在线教学平台，通过自建与共享等方式丰富线上教学资源尤其是各外语类教学资源，探索信息技术与课堂教学的深度融合的模式。 评价方案3是针对表现较差的学校（各方面排名均位于后三分之一）。建议：加强教学常规工作，着力提升课堂教学质量。学校改进应以提升课堂教学质量为主，可从以下几方面入手：（1）在落实课程方案方面，加强课程与教学基础理论的学习，通过有针对性的外出研修、互访参学及内部研讨，切实提升全体教师特别是学校管理团队及教研骨干的课程领导力、设计力及执行力，探索优化适合学校未来发展的课程方案。（2）在规范教学实施方面，加强课后辅导，提供多样化的学习资源和帮助，让学生有更多的机会进行自主学习和提高。（3）在优化教学方式方面，充实校园信息化硬件，全面构建信息化管理平台，创新教师现代教育技术使用的培训模式，举办现代教育技术课堂教学比赛，加强师生信息素养的提升。参考上述3个评价方案时，注意根据这所学校每个方面的实际排名情况，从不同的评价方案中选择与之相对应的建议。不要简单的重复评价方案的句子。\", \"category\": 0}]}, {\"id\": \"22afd6e0-7d25-4754-995f-cac0d5045f16\", \"name\": \"教师发展方面\", \"needed\": 0, \"prompts\": [{\"id\": \"cc4e9d7f-e481-4d85-89b1-0300f5c1ac0a\", \"name\": \"定义角色\", \"content\": \"作为教育评价专家，请根据以下学校的情况提出改进建议。这所学校的不同方面的表现排名数据如下：\", \"category\": 0}, {\"id\": \"ccda8729-d276-4abd-9714-7974099ca7cf\", \"name\": \"排名数据\", \"category\": 1}, {\"id\": \"694b958e-b128-438c-98bb-0d7ccb2b6fe1\", \"name\": \"学校数量\", \"content\": \"一共有36所学校参与排名。\", \"category\": 0}, {\"id\": \"4ca6e369-5253-4ea5-8185-522f5427aa5f\", \"name\": \"薄弱的方面\", \"content\": \"该校在以下方面表现较差：。需要特别关注这些表现较差的方面，并在建议中特别说明针对这些表现较差的方面的解决方案。\", \"category\": 0}, {\"id\": \"fad51664-3b17-4781-bd5d-87134261cd48\", \"name\": \"参考文本\", \"content\": \"参考以下3个评价方案的内容与结构，根据学校实际情况，提供针对性建议，字数500字。确保建议与学校实际情况相符，勿提供虚假信息。 评价方案1是针对各方面表现优秀的学校（各方面排名均位于前三分之一）。建议：注重培育学科带头人，提升学校影响力。学校改进应以培育学科带头人，提升学校影响力为主，可从以下几方面入手：（1）在教师专业成长方面，学校要考虑在促进全体教师发展的同时，可重点培养学科知名教师。如通过走出学校，让教师在区域以及全国的平台上学习、交流、上公开课、赛课、做讲座、发表论文等方式，以及边实践、边阅读、边反思的方式等，让部分学科骨干教师从优秀走向卓越，在区域范围内有更大的影响力，带动本校以及区域内学科的发展。（2）在健全教师激励机制方面，可精准识别教师需求，采集教师激励期待数据进行分析，归纳不同属性教师期待与需求。科学设计教师激励标准，强调层级递进激励，构建全面激励体系，力求建立“类别清晰、引导有序、持续有效、整体有感”的教师激励体系。确保学校组织激励与教师自我激励的并置同构。评价方案2是针对表现一般的学校（各方面排名均位于中间三分之一）。建议：融通多方资源，充分激活团队潜力。学校改进应以融通多方资源，充分激活团队潜力为主，可从以下几方面入手：（1）在教师专业成长方面，进一步完善学校的梯级培养机制，推进建设“校级教学能手→校级教学新秀→校级骨干教师→校级名师”等梯队式、层进式教师培养模式，推动青年教师的快速成长。加强校际交流，以各学科带头人为中心成立校级教师互学互助小组，定期互相听评课，围绕课堂准备、课堂教学与课后复盘展开研讨，在相互学习中提升自我专业素养。（2）在健全教师激励机制方面，建议学校在“十四五”规划执行中，落实专门针对教师专业发展的方案；并且在政策及财政许可的范围之内，优化评优、晋级及相应激励机制，让更多教师参与到学校管理过程之中，充分激活团队活力。 评价方案3是针对表现较差的学校（各方面排名均位于后三分之一）。建议：突破师资队伍瓶颈，助力学校跨越发展。学校改进应以突破师资队伍瓶颈，助力学校跨越发展为主，可从以下几方面入手：（1）加强师德师风建设，重点培育教师职业认同感和责任感，通过多层次的教师培训平台和途径，打造一支“有理想信念、有道德情操、有扎实学识、有仁爱之心”的教师队伍，营造风清气正的教师风气。（2）在教师专业成长方面，建议通过制定学校层面的《学校教师发展规划》，引导每位教师制定个人的专业发展规划，明晰学校和教师个人三年或五年的发展目标、发展路径以及所需的支持，让教师队伍建设的举措和方法更具有针对性。）（3）在教师激励方面，可以通过教师节时组织尊师重教的系列活动、表扬教师的过程性成就而不仅仅是最终结果、评价教师不唯分数唯考试成绩等来提升教师职业成就感，增强教师团队的凝聚力和向心力。参考上述3个评价方案时，注意根据这所学校每个方面的实际排名情况，从不同的评价方案中选择与之相对应的建议。不要简单的重复评价方案的句子。\", \"category\": 0}]}, {\"id\": \"24dc874e-d1b7-432e-b8b4-d152e47fe991\", \"name\": \"学校管理方面\", \"needed\": 0, \"prompts\": [{\"id\": \"cf622b41-2800-4076-aa6f-587c43d18e8a\", \"name\": \"定义角色\", \"content\": \"作为教育评价专家，请根据以下学校的情况提出改进建议。这所学校的不同方面的表现排名数据如下：\", \"category\": 0}, {\"id\": \"1a370ed8-0014-4b7a-b931-73851f706806\", \"name\": \"排名数据\", \"category\": 1}, {\"id\": \"3041b004-5f40-49c1-b980-060ee288fd63\", \"name\": \"学校数量\", \"content\": \"一共有36所学校参与排名。\", \"category\": 0}, {\"id\": \"d469d5e2-f902-4226-bdb8-294ea2363499\", \"name\": \"薄弱方面\", \"content\": \"该校在以下方面表现较差：。需要特别关注这些表现较差的方面，并在建议中特别说明针对这些表现较差的方面的解决方案。\", \"category\": 0}, {\"id\": \"cfd6b37d-d86a-453e-98fd-7373274766ed\", \"name\": \"参考文本\", \"content\": \"参考以下3个评价方案的内容与结构，根据学校实际情况，提供针对性建议，字数500字。确保建议与学校实际情况相符，勿提供虚假信息。 评价方案1是针对各方面表现优秀的学校（各方面排名均位于前三分之一）。建议：聚焦高位突破发展，发挥区域示范效应。学校改进可从聚焦高位突破发展，发挥区域示范效应为主，可从以下几方面入手：（1）在完善内部治理方面，需要学校领导带领全体教职员工梳理学校发展的高标意识和价值追求，共同总结梳理学校辉煌办学的经验、学校进一步发展面临的优势和不足、机遇与挑战，学校高品质发展的路径和策略。（2）在加强校园文化建设方面，注重融汇中华传统文化、地方文化、航空航天文化及当代文化，不使偏废，探索西部学校特色文化建设新路；此外，在具体推进中，需要打通办学思路和组织架构之间的隔阂，并且探索将文化建设与德育活动、课程思政相互贯通；必要之时，还可以学校设置重点课题的形式专门推进，并为学校申报、承接更高级别的科研项目，培育教学成果奖项奠定基础。评价方案2是针对表现一般的学校（各方面排名均位于中间三分之一）。建议：发掘学校文化理念，引领学校工作全局。学校改进可从进一步发掘学校文化理念，引领学校工作全局为主，可从以下几方面入手：（1）在完善内部治理方面，优化学校对家庭教育的指导，形成家校社的教育合力，可通过定期召开学校、年级以及班级的家长会，以及学校公众号、班级微信群等新媒介，宣传学校办学理念，公开学校的办学实践，取得家长对学校办学实践的理解和认同、并指导家长和学校一起共同养育孩子。（2）在加强校园文化建设方面，加强校园文化氛围的营造。通过组织各种形式的活动、展览、演出等活动，让学校的师生感受到校园文化的魅力，增强师生的归属感和凝聚力。评价方案3是针对表现较差的学校（各方面排名均位于后三分之一）。建议：完善学校发展规划，实现学校内涵式发展。学校改进可从完善学校发展规划，实现学校内涵式发展为主，可从以下几方面入手：（1）在完善内部治理方面，针对制度建设，需要立足校情学情，继续坚持德智体美劳“五育”并举，融合育人，建立健全落实机制，并根据新中考改革方案，积极深化内部改革，不断增强自我发展功能，提高人才培养质量和效益，保证办学规划和办学目标的顺利实施。（2）在加强校园文化建设方面，需要将学校文化精神外化于学校标识、校园环境、班级环境之中，内化于学校管理、课程教学及师生日常之中，在具体推进中，需要打通办学思路和组织架构之间的隔阂，并且探索将文化建设与德育活动、课程思政相互贯通。参考上述3个评价方案时，注意根据这所学校每个方面的实际排名情况，从不同的评价方案中选择与之相对应的建议。不要简单的重复评价方案的句子。\", \"category\": 0}]}, {\"id\": \"625bd4a3-bda7-4222-b763-f6b842231482\", \"name\": \"学生发展方面\", \"needed\": 0, \"prompts\": [{\"id\": \"1ee6ad60-339e-4538-80be-16dc7b20de33\", \"name\": \"定义角色\", \"content\": \"作为教育评价专家，请根据以下学校的情况提出改进建议。这所学校的不同方面的表现排名数据如下：\", \"category\": 0}, {\"id\": \"bfd0a961-6590-4ed2-ad3e-bef207d2036a\", \"name\": \"排名数据\", \"category\": 1}, {\"id\": \"b6dd9c22-2d65-45e5-a5af-daa82b6c4b57\", \"name\": \"学校数量\", \"content\": \"一共有36所学校参与排名。\", \"category\": 0}, {\"id\": \"b8063f79-6903-4341-8e25-f6569090014d\", \"name\": \"薄弱方面\", \"content\": \"该校在以下方面表现较差：。需要特别关注这些表现较差的方面，并在建议中特别说明针对这些表现较差的方面的解决方案。\", \"category\": 0}, {\"id\": \"fb8eeeb6-04b9-463e-b67e-257c5f3c5806\", \"name\": \"参考文本\", \"content\": \"参考以下3个评价方案的内容与结构，根据学校实际情况，提供针对性建议，字数500字。确保建议与学校实际情况相符，勿提供虚假信息。 评价方案1是针对各方面表现优秀的学校（各方面排名均位于前三分之一）。建议：立足教书育人，进一步促进全面发展。学校改进应以进一步促进全面发展为主，可从以下几方面入手：（1）在学生综合素质档案建设和使用情况方面，进一步完善学生综合素质档案建设，细化学生综合素质评价指标，提升学生多方面能力，切实促进学生的全面发展。（2）在师生及家长满意度方面，立足于教书育人的主线，营造良性育人环境，使师生关系更加和谐，在关注学生学习成绩的同时，通过更加有效的方式增强学生独立思考和解决问题的意识和能力。评价方案2是针对表现一般的学校（各方面排名均位于中间三分之一）。建议：发挥学校作用，提升全面发展质量。学校改进应以提升全面发展质量为主，可从以下几方面入手：（1）在学生综合素质档案建设和使用情况方面，要根据专业指导对学生多方面综合素质进行评价，并切实发挥学生综合素质档案的作用，利用档案中的信息指导学生发展。（2）在师生及家长满意度方面，教师及时调整教学思路，加强教学监控与教学流程的动态管理，建立民主、平等、合作的新型师生关系，渗透素质教育和新课标精神，努力使不同性格禀赋、不同兴趣特长、不同素质潜力的学生都能接受符合自己成长需要的教育，切实提高师生、家长、社会等方面对学校办学质量的满意度。评价方案3是针对表现较差的学校（各方面排名均位于后三分之一）。建议：家校社合力，促进学生健康成长。学校改进应以提升全面发展质量为主，可从以下几方面入手：（1）在学生综合素质档案建设和使用情况方面，建立完善覆盖全员的综合素质档案，定期检查和评估，以便尽早发现并动态跟进其中需要特别关注者；适当提升实践类、活动类作业的比例，减少书面作业的时间和比例。（2）在师生及家长满意度方面，建立更为长效稳定的家校合作机制，比如建设家长学校，常态化家委会工作，形成家校共育工作的常态化机制。合理整合运用学生家长的优质资源，为学校的发展贡献更大更实的力量。参考上述3个评价方案时，注意根据这所学校每个方面的实际排名情况，从不同的评价方案中选择与之相对应的建议。不要简单的重复评价方案的句子。\", \"category\": 0}]}, {\"id\": \"cb37c0fa-adca-4f84-8694-61fe1914735d\", \"name\": \"五项管理+双减\", \"needed\": 0, \"prompts\": [{\"id\": \"63a1c4ca-7bfd-446c-add4-d8e53f11f160\", \"name\": \"角色定义\", \"content\": \"作为教育评价专家，请根据以下学校的情况提出改进建议。这所学校的不同方面的表现排名数据如下：\", \"category\": 0}, {\"id\": \"937938f7-b170-4297-863e-6a8e04eeeefc\", \"name\": \"排名数据\", \"category\": 1}, {\"id\": \"78e791f7-75a7-4bb6-a27e-47229422a2db\", \"name\": \"学校数量\", \"content\": \"一共有36所学校参与排名。\", \"category\": 0}, {\"id\": \"47cfca90-4884-4c3e-9ae1-1f4995d187f4\", \"name\": \"参考文本\", \"content\": \"参考以下2个评价方案的内容与结构，根据学校实际情况，提供针对性建议，字数500字。确保建议与学校实际情况相符，勿提供虚假信息。 评价方案1是针对各方面表现优秀的学校（各方面排名均位于前三分之一）。建议：改进管理办法，发挥学校主体作用。学校改进应以改进管理办法为主，可从以下几方面入手：（1）在五项管理方面，深入研究“五项管理”相关文件精神，充分征求学生、家长、教师的意见建议，形成统一目标，要把握好管理的边界和度，注意细节和方法，坚持“刚性约束”和“柔性管理”相结合，要求要合情合理，问题要妥善处理，防止简单化处理，搞“一刀切”，激化矛盾。（2）在双减方面，学校进一步优化课堂教学、作业管理与课后服务，以提升学校教育的质量和效率；灵活调整教师工作时间，提升教师工作效率，从而更好地发挥其在落实“双减”中的枢纽作用。评价方案2是针对表现一般的学校（各方面排名均位于后三分之二）。建议：出台工作方案，指导工作落实。学校应该出台具体的工作方案，以指导管理工作的落实为主，可从以下几方面入手：（1）出台“五项管理“工作方案，并切实予以实施，落实高质量课后服务，减轻学生过重的课业负担和教师过重的工作压力，多途径改善学生和教师的学习、工作情绪。（2）在具体实施中，牢牢抓住”作业管理“这个“双减”和“五项管理“都涉及的专题，切实提升学科书面作业布置的质量。（3）要大力加强手机管理，尽量做到手机不进校园，即使进了校园，要有专人、专门地方保存。（4）要和家长一起努力保障初中生9个小时的睡眠。（5）学校领导团队在研究和讨论的基础上，出台学校“五项管理”和“双减”的具体实施方案，尤其是强化课后服务活动的高质量开展。参考上述2个评价方案时，注意根据这所学校每个方面的实际排名情况，从不同的评价方案中选择与之相对应的建议。不要简单的重复评价方案的句子。\", \"category\": 0}]}, {\"id\": \"c9202159-3799-44ad-938d-51dbfb3001d2\", \"name\": \"学生心理健康\", \"needed\": 0, \"prompts\": [{\"id\": \"76b53846-4626-47a1-8940-ea2100124294\", \"name\": \"确定角色\", \"content\": \"作为教育评价专家，请根据以下学校的情况提出改进建议。这所学校的不同方面的表现排名数据如下：\", \"category\": 0}, {\"id\": \"fa4aac9b-e1a8-42d3-9a3d-e0518b1bb733\", \"name\": \"排名数据\", \"category\": 1}, {\"id\": \"d927a509-4204-47fc-a89f-0df3ddfb238c\", \"name\": \"学校数量\", \"content\": \"一共有36所学校参与排名。\", \"category\": 0}, {\"id\": \"8e9943ef-5195-4300-a321-91c2917a4082\", \"name\": \"参考文本\", \"content\": \"参考以下2个评价方案的内容与结构，根据学校实际情况，提供针对性建议，字数500字。确保建议与学校实际情况相符，勿提供虚假信息。 评价方案1是针对各方面表现优秀的学校（各方面排名均位于前三分之一）。建议：改进工作办法，预防学生心理问题出现。学校改进应以预防学生心理问题的出现为主，可从以下几方面入手：（1）学校要加强人文关怀、专业辅导等方面工作的针对性，及时解决学生出现的心理健康问题。（2）常规心理教育及特殊心理辅导并重，进一步建立完善学生心理健康档案，以及与之相应的危机应对及干预机制。（3）学校进一步密切家校联系，把握学生学业及健康动态。（4）学校在确保开足国家规定的艺体类课程的前提下，应当面向全校师生，更加注重传授身心健康教育及卫生保健知识。评价方案2是针对表现一般的学校（各方面排名均位于后三分之二）。建议：加大重视，关注学生身心健康。学校改进应以增加对学生心理健康问题的关注度为主，可从以下几方面入手：（1）争取政策支持，配齐配足学校心理健康教育设施及师资，切实开展常规心理教育及特殊心理辅导，纳入学校常规工作管理。（2）借助地方政府及村（社）居委力量，全面掌握学生家庭情况，在保持家校常规联络的同时，对于随班就读、单亲及留守儿童等需要特殊关注和照护的学生，落实专班责任制。参考上述2个评价方案时，注意根据这所学校每个方面的实际排名情况，从不同的评价方案中选择与之相对应的建议。不要简单的重复评价方案的句子。\", \"category\": 0}]}, {\"id\": \"d1ea7db8-16fe-4677-b1b2-074801245028\", \"name\": \"教师职业倦怠\", \"needed\": 0, \"prompts\": [{\"id\": \"0c42fcd2-98b7-4378-8bbb-7219abfb5df7\", \"name\": \"确定角色\", \"content\": \"作为教育评价专家，请根据以下学校的情况提出改进建议。这所学校的不同方面的表现排名数据如下：\", \"category\": 0}, {\"id\": \"ce3bddf6-6547-42b9-a31a-6d8bd21d8e6c\", \"name\": \"排名数据\", \"category\": 1}, {\"id\": \"b550d049-1fb9-4094-b8d4-9ceeebd25cde\", \"name\": \"学校数量\", \"content\": \"一共有36所学校参与排名。\", \"category\": 0}, {\"id\": \"5d2b796d-c643-4106-b126-7a2b230bb326\", \"name\": \"参考文本\", \"content\": \"参考以下2个评价方案的内容与结构，根据学校实际情况，提供针对性建议，字数500字。确保建议与学校实际情况相符，勿提供虚假信息。评价方案1是针对各方面表现优秀的学校（各方面排名均位于前三分之一）。建议：提升教师成就感，激发教师工作热情。学校改进可从进一步提升教师成就感、激发广大教师的教育情怀和工作热情入手，例如：（1）在提升教师职业成就感方面，可激励教师开展教学研究和教育实践，鼓励教师参加教育培训和学术交流活动，提高教师的专业技能水平。给予教师公正的评价和肯定，例如通过优秀教师评选、表彰等形式，增强教师的职业认同感和成就感。（2）在提升教师归属感方面，可引导教师结合个人实际情况、学校发展需求，制定可实现、可操作的个人发展规划，在此过程中，学校多维度为教师发展提供支持和保障。鼓励教师参加学校、区级以及市级的教师培训，并重点强化校本研修对于教师专业发展的促进作用，以学校为主阵地，基于常规课堂教学实践来改进教师的教学技能和专业素养。评价方案2是针对表现一般的学校（各方面排名均位于后三分之二）。建议：多措并举减轻教师压力，关注教师健康成长。学校改进可从多措并举减轻教师压力，关注教师健康成长入手，例如：（1）在减轻教师压力方面，可合理规划教师的工作任务和教学计划，确保教师有充足的时间和精力完成工作任务。提供适当的支持和资源，如行政助理、教学助理等，以减轻教师的工作负担。改进管理制度和流程，简化教师的行政事务，避免教师在行政方面耗费过多时间和精力。（2）在提升教师情绪情感方面，可建立心理健康支持体系，提供心理咨询、心理辅导等服务，帮助教师处理工作和生活中的压力和困难。建立良好的工作氛围和人际关系，加强教师之间的交流和互动，减少教师之间的矛盾和冲突。培养教师积极的情绪状态，例如通过组织文娱活动、体育比赛等方式，增强教师的身心健康和幸福感。参考上述2个评价方案时，注意根据这所学校每个方面的实际排名情况，从不同的评价方案中选择与之相对应的建议。不要简单的重复评价方案的句子。\", \"category\": 0}]}]}','2023-04-17 14:03:48'),('08a03a03-43a9-40e9-9fcf-dd6315c6eddc','{\"steps\": [{\"id\": \"f9beaf35-91a3-474c-a40c-f44279c729e2\", \"desc\": \"分析优势与不足之处，然后根据不足的方面生成改进建议的文本内容，并对文本内容的字数提出了限制\", \"name\": \"获得改进建议文本内容\", \"needed\": 1, \"prompts\": [{\"id\": \"8859150e-eee3-45d5-bffe-7a503498835b\", \"desc\": \"学校在某个指标中的表现，如果下面有许多二级指标，需要把二级指标的表现文本都带上。\", \"name\": \"指定角色，分配任务\", \"relyid\": \"\", \"content\": \"现在你是一位教育专家，你要根据下面给出的有关某所中学的办学质量评价的文本内容，分析这所中学在那些方面表现的好，在哪些方面表现的差。然后综合分析这所中学表现差的方面，为这所中学写一段改进建议，改进建议的字数不要低于600字。\", \"category\": 0}, {\"id\": \"50243388-19db-4542-a7ff-668a1451003b\", \"name\": \"某中学在某一级指标上的排名等描述性文本内容\", \"des c\": \"学校在某个指标中的表现，如果下面有许多二级指标，需要把二级指标的表现文本都带上。\", \"relyid\": \"\", \"content\": \"\", \"category\": 1}]}, {\"id\": \"7d55f521-fd8c-42a0-a3b1-5c90645f382e\", \"desc\": \"得到一条改进建议的标题\", \"name\": \"生成改进建议标题\", \"needed\": 0, \"prompts\": [{\"id\": \"9a944e49-f0ca-4bae-a2da-94d5c8c5a608\", \"desc\": \"说明任务\", \"name\": \"说明任务，即对文本进行摘要，进而得到标题\", \"relyid\": \"f9beaf35-91a3-474c-a40c-f44279c729e2\", \"content\": \"下面是专家为某所中学提出的改进建议文本内容，请将这些内容概括为若干个短句，例如深入推进国家课程校本化实施，提升领导团队课程领导力，促进学校内涵发展、提升领导团队的课程教学领导力，促进学校内涵发展等。\", \"category\": 0}, {\"id\": \"c3b7c40e-4e45-4c8a-b736-3db2d67b8398\", \"desc\": \"由上一步生成的改进建议文本内容\", \"name\": \"改进建议文本\", \"relyid\": \"f9beaf35-91a3-474c-a40c-f44279c729e2\", \"content\": \"\", \"category\": 2}]}, {\"id\": \"24361ecd-cb5b-43af-874e-4bc85a99eb50\", \"desc\": \"将该指标内涵的文本部分融入到改进建议文本当中，使得改进建议当中包含部分阐述改进重要性的文本内容。\", \"name\": \"将上位概念融入改进建议文本内容中\", \"needed\": 1, \"prompts\": [{\"id\": \"3739de33-8ab7-4299-8f68-7135455e5165\", \"desc\": \"某个一级指标以及下面的二级指标的内涵、意义描述文本。可以从政策文本中引用。\", \"name\": \"派送任务\", \"relyid\": \"\", \"content\": \"下面的文本内容一是对一些指标的重要性的阐释，文本内容二是某位专家为某所中学提出的办学改进建议。现在你要以文本内容二为主，从文本内容一中选择一些内容并融入到文本内容二中，形成一段不少于600字的文本，注意不要改变文本内容二的意思。文本内容一如下：\", \"category\": 0}, {\"id\": \"d4e882f1-9357-44d7-979c-4aa7b0ee7dc5\", \"desc\": \"某个一级指标以及下面的二级指标的内涵、意义描述文本。可以从政策文本中引用。\", \"name\": \"阐释某项评价指标的内涵、意义的文本\", \"relyid\": \"\", \"content\": \"\", \"category\": 1}, {\"id\": \"64723a30-a0de-4276-8648-fd99101c9a7f\", \"desc\": \"某个一级指标以及下面的二级指标的内涵、意义描述文本。可以从政策文本中引用。\", \"name\": \"派送任务\", \"relyid\": \"\", \"content\": \"文本内容二如下：\", \"category\": 0}, {\"id\": \"d649f867-e6a1-4c10-8e65-ba4471fb181e\", \"desc\": \"由第一步生成的改进建议的文本内容\", \"name\": \"已经生成的改进建议文本\", \"relyid\": \"f9beaf35-91a3-474c-a40c-f44279c729e2\", \"content\": \"\", \"category\": 2}]}, {\"id\": \"690fcdf3-3ba1-45c1-8857-8c5464ff5c30\", \"desc\": \"将学校目前的真实表现情况文本部分融入到改进建议文本当中，使得改进建议文本当中包含对学校具体情况的分析语句。\", \"name\": \"结合学校实际修改学校改进建议的文本内容\", \"needed\": 0, \"prompts\": [{\"id\": \"fc819f30-096d-46c8-923a-c083daafbace\", \"desc\": \"告知模型应该完成的内容\", \"name\": \"派送任务\", \"relyid\": \"\", \"content\": \"下面的文本内容一是某所中学在实际的教学工作中的自我评价，文本内容二是一段为该中学在办学方面需要改进的内容提出的改进建议。现在你要根据文本内容一分析这所中学有哪些优势，然后将这些优势适当的加入到文本内容二中，使得文本内容二不仅仅包含了对这所中学表现的不好的方面的改进建议，还有对这所学校表现的好的方面的肯定。注意整合后的文本不要偏离文本内容二的意思，整合后的文本的字数不少于600字。文本内容一如下：\", \"category\": 0}, {\"id\": \"92ee9198-7e11-4935-a768-12312341657d\", \"desc\": \"学校在各个方面的表现的文本描述\", \"name\": \"学校实际办学表现的文本内容\", \"relyid\": \"\", \"content\": \"\", \"category\": 1}, {\"id\": \"57eb044f-b1b2-441b-8370-cf2d8fc795cd\", \"desc\": \"\", \"name\": \"文本内容二提示文本\", \"relyid\": \"\", \"content\": \"文本内容二如下：\", \"category\": 0}, {\"id\": \"3e1d9150-326b-4fc2-af9b-1cd6a06fde88\", \"desc\": \"由上一步得到的改进建议文本\", \"name\": \"融合了概念文本的改进建议内容\", \"relyid\": \"24361ecd-cb5b-43af-874e-4bc85a99eb50\", \"content\": \"\", \"category\": 2}]}, {\"id\": \"b9fe5962-2fa6-4e9e-9114-ee25559745d9\", \"desc\": \"调整改进建议的用词，以及改进建议的语言风格等。\", \"name\": \"调整改进建议文本的用词、语言风格、通顺程度等\", \"needed\": 0, \"prompts\": [{\"id\": \"2eca438c-52f8-460e-9ab2-1af05c6b9407\", \"desc\": \"告知模型要调整文本内容\", \"name\": \"派送任务\", \"relyid\": \"\", \"content\": \"以下内容是某位专家对某个中学的办学提出的改进建议文本。你要对文本内容进行修改，在不改变文本原有的意思的前提下，确保修改后的文本内容中没有人称代词，保证文本的语气要温和、要体现[建议]而不是[批评]。\", \"category\": 0}, {\"id\": \"369fb1e0-025a-4932-a694-d80e2d9fa059\", \"desc\": \"融合了指标含义、概念，以及包含了学校实际办学情况的改进建议文本内容，由上一步得到\", \"name\": \"修改了两次之后的改进建议文本内容\", \"relyid\": \"24361ecd-cb5b-43af-874e-4bc85a99eb50\", \"content\": \"\", \"category\": 2}]}]}','2023-05-11 03:38:31'),('6081a286-595f-415a-84c1-ed84502cf30f','{\"steps\": [{\"id\": \"f9beaf35-91a3-474c-a40c-f44279c729e2\", \"desc\": \"分析优势与不足之处，然后根据不足的方面生成改进建议的文本内容，并对文本内容的字数提出了限制\", \"name\": \"获得改进建议文本内容\", \"needed\": 1, \"prompts\": [{\"id\": \"8859150e-eee3-45d5-bffe-7a503498835b\", \"desc\": \"学校在某个指标中的表现，如果下面有许多二级指标，需要把二级指标的表现文本都带上。\", \"name\": \"指定角色，分配任务\", \"relyid\": \"\", \"content\": \"现在你是一位教育专家，你要根据下面给出的有关某所中学的办学质量评价的文本内容，分析这所中学在那些方面表现的好，在哪些方面表现的差。然后综合分析这所中学表现差的方面，为这所中学写一段改进建议，改进建议的字数不要低于600字。\", \"category\": 0}, {\"id\": \"50243388-19db-4542-a7ff-668a1451003b\", \"name\": \"某中学在某一级指标上的排名等描述性文本内容\", \"des c\": \"学校在某个指标中的表现，如果下面有许多二级指标，需要把二级指标的表现文本都带上。\", \"relyid\": \"\", \"content\": \"\", \"category\": 1}]}, {\"id\": \"7d55f521-fd8c-42a0-a3b1-5c90645f382e\", \"desc\": \"得到一条改进建议的标题\", \"name\": \"生成改进建议标题\", \"needed\": 0, \"prompts\": [{\"id\": \"9a944e49-f0ca-4bae-a2da-94d5c8c5a608\", \"desc\": \"说明任务\", \"name\": \"说明任务，即对文本进行摘要，进而得到标题\", \"relyid\": \"f9beaf35-91a3-474c-a40c-f44279c729e2\", \"content\": \"下面是专家为某所中学提出的改进建议文本内容，请将这些内容概括为若干个短句，例如深入推进国家课程校本化实施，提升领导团队课程领导力，促进学校内涵发展、提升领导团队的课程教学领导力，促进学校内涵发展等。\", \"category\": 0}, {\"id\": \"c3b7c40e-4e45-4c8a-b736-3db2d67b8398\", \"desc\": \"由上一步生成的改进建议文本内容\", \"name\": \"改进建议文本\", \"relyid\": \"f9beaf35-91a3-474c-a40c-f44279c729e2\", \"content\": \"\", \"category\": 2}]}, {\"id\": \"24361ecd-cb5b-43af-874e-4bc85a99eb50\", \"desc\": \"将该指标内涵的文本部分融入到改进建议文本当中，使得改进建议当中包含部分阐述改进重要性的文本内容。\", \"name\": \"将上位概念融入改进建议文本内容中\", \"needed\": 1, \"prompts\": [{\"id\": \"3739de33-8ab7-4299-8f68-7135455e5165\", \"desc\": \"某个一级指标以及下面的二级指标的内涵、意义描述文本。可以从政策文本中引用。\", \"name\": \"派送任务\", \"relyid\": \"\", \"content\": \"下面的文本内容一是对一些指标的重要性的阐释，文本内容二是某位专家为某所中学提出的办学改进建议。现在你要以文本内容二为主，从文本内容一中选择一些内容并融入到文本内容二中，形成一段不少于600字的文本，注意不要改变文本内容二的意思。文本内容一如下：\", \"category\": 0}, {\"id\": \"d4e882f1-9357-44d7-979c-4aa7b0ee7dc5\", \"desc\": \"某个一级指标以及下面的二级指标的内涵、意义描述文本。可以从政策文本中引用。\", \"name\": \"阐释某项评价指标的内涵、意义的文本\", \"relyid\": \"\", \"content\": \"\", \"category\": 1}, {\"id\": \"64723a30-a0de-4276-8648-fd99101c9a7f\", \"desc\": \"某个一级指标以及下面的二级指标的内涵、意义描述文本。可以从政策文本中引用。\", \"name\": \"派送任务\", \"relyid\": \